def binarySearch(list, value):
    low = 0
    high = len(list)-1
    while low <= high:
        mid = (low + high) // 2
        if list[mid] == value:
            return mid
        elif list[mid] > value:
            high = mid - 1
        else:
            low = mid + 1
    return -1

