  for char in input_string:
    if char not in count_dict:
      count_dict[char] = 0
    count_dict[char] += 1
  return count_dict

"""
Python implementation of the Fibonacci sequence using memoization to optimize performance
"""

def fibonacci(n):
    # Base case
    if n <= 1:
        return n

    # Recursive case
    if n == 2:
        return 1
    else:
        return fibonacci(n-1) + fibonacci(n-2)


