def get_shortest_path(graph, s, t):
    path = [[s]]
    queue = [(s, 0)]
    while queue:
        node, distance = queue.pop()
        for neighbor, weight in graph[node].items():
            if distance + weight < 0:
                continue
            queue.append((neighbor, weight))
        path.append(node)
    return path

