def dynamic_programming(s, t):
    dp = [0 for _ in range(max(0, len(s) - len(t)) + 1)]
    for i in range(0, len(s)):
        for j in range(0, len(t)):
            if i == 0 or j == 0:
                dp[i + 1] = 0
                dp[j + 1] = 0
            elif s[i] == t[j]:
                dp[i + 1] = max(dp[i + 1], dp[j + 1] + 1)
            else:
                dp[j + 1] = max(dp[i + 1], dp[j + 1] + 1)
    return dp[max(0, len(s) - len(t)) + 1]

