def longest_common_subsequence(lst1, lst2):
    """Finds the longest common subsequence between two strings using dynamic programming."""
    if len(lst1)!= len(lst2):
        return None

    n = len(lst1)

    dp = [[0 for _ in range(n+1)] for _ in range(len(lst2)+1)]

    dp[0][0] = 1
    dp[0][1] = lst1[0]

    for i in range(1, n):
        for j in range(1, n):
            if lst1[i-1] == lst2[j-1]:
                dp[i][j] = dp[i-1][j-1] + 1
            else:
                dp[i][j] = max(dp[i-1][j], dp[i][j-1]) + 1

    return dp[n][n]

