def binary_search(list, target):
    first = 0
    last = len(list) - 1
    while first <= last:
        mid = (first + last) // 2
        if list[mid] == target:
            return mid
        if list[mid] < target:
            first = mid + 1
        else:
            last = mid - 1

    # If `list[first]` is not equal to `target`, then `first` is not the 
